/*
    The purpose of this file is to provide functionality of the resource page.
    It allows the user to choose a category and the resource page will display the
    relevant resources relevant to the chosen category.
*/
$(document).ready(function(){var e=document.getElementsByClassName("category-link"),t=document.getElementsByClassName("resource-card")
$(e[0]).click(function(){for(var a=0;a<e.length;a++)$(e[a]).removeClass(" is-active")
$(this).addClass(" is-active")
for(var r=0;r<t.length;r++)$(t[r]).show()})
for(var a=1;a<e.length;a++)$(e[a]).click(function(){for(var a=0;a<e.length;a++)$(e[a]).removeClass(" is-active")
$(this).addClass(" is-active")
for(var r=0;r<t.length;r++)for(var s=t[r].querySelectorAll(".tag"),l=0;l<s.length;l++){var i=$(s[l]).html(),n=$(this).html()
if(i.indexOf(n)>=0){$(t[r]).show()
break}$(t[r]).hide()}})})
